Bad:


import threading
import time

def print_time(threadName, delay, counter):
    lock.acquire()
    print 'Staring', threadName
    lock.release()
    while counter:
        time.sleep(delay)
        lock.acquire()
        print '%s: %s' % (threadName, time.ctime(time.time()))
        lock.release()
        counter -= 1
    lock.acquire()
    print 'Exiting', threadName
    lock.release()

lock = threading.Lock()

t1 = threading.Thread(target=print_time, args=('Thread-1', 1, 5,))
t2 = threading.Thread(target=print_time, args=('Thread-2', 2, 5,))

t1.start()
t2.start()

t1.join()
t2.join()

print 'Exiting Main Thread'




Good:


import threading
import time

def print_time(threadName, delay, counter):
    with lock:
        print 'Staring', threadName
    while counter:
        time.sleep(delay)
        with lock:
            print '%s: %s' % (threadName, time.ctime(time.time()))
        counter -= 1
    with lock:
        print 'Exiting', threadName

lock = threading.Lock()

t1 = threading.Thread(target=print_time, args=('Thread-1', 1, 5,))
t2 = threading.Thread(target=print_time, args=('Thread-2', 2, 5,))

t1.start()
t2.start()

t1.join()
t2.join()

print 'Exiting Main Thread'




